import React, { useState, useEffect } from 'react'
import { withRouter, useHistory } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import { login, signup } from '../redux/user/actions'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee, faSync } from '@fortawesome/free-solid-svg-icons'
import { Spinner } from 'react-bootstrap';
import Card from '../components/UIElemets/Card';
import Register from './Register';
const LoginPage = ({ setupSocket }) => {
    const dispatch = useDispatch()
    const { user, error, loading } = useSelector(state => state.user)
    const [isLoginMode, setIsLoginMode] = useState(true)   // const auth = useContext(AuthContext)
    const [userDetails, setUseDetails] = useState({})
    const history = useHistory()
    const [errorMessage, setErrorMessage] = useState('')


    const hadleChange = (e) => {
        setUseDetails({ ...userDetails, [e.target.name]: e.target.value })
    }

    useEffect(() => {
        if (user) {
            setupSocket()
            history.push("/conversations");
        }
    }, [])
    useEffect(() => {
        if (user) {
            history.push("/conversations");
        } else {
            setErrorMessage(error)
        }
    }, [user, error, loading])
    const loginUser = async (e) => {
        console.log('user', e)
        e.preventDefault()
        const { email, password } = userDetails
        if (isLoginMode) {
            dispatch(login(email, password))
        } else {
            dispatch(signup(userDetails))
        }
    };
    const switchModehandler = () => {
        setIsLoginMode(!isLoginMode)
    }

    return (
        < >
            <h2 >{isLoginMode ? 'Login' : 'Sign-Up'}</h2>
            {errorMessage && <p>{errorMessage}</p>}
            {loading && <p>loading</p>}
            <hr />
            <div class="row">
                <div class="book">
                    <div class="book__form">
                        <form action="#" class="form">
                            <div class="u-center-text u-margin-bottom-medium">
                                <h2 class="heading-secondary">Start messaging now</h2>
                                <h5>login</h5>
                            </div>
                            <div class="form__group">
                                <input
                                    type="email"
                                    class="form__input"
                                    required
                                    id="email"                                  
                                    placeholder="abc@example.com"
                                    onChange={hadleChange}
                                    value={userDetails.email}
                                />
                                <label for="name" class="form__label">Full name</label>
                            </div>
                            <div class="form__group">
                                <input
                                    type="email"
                                    class="form__input"
                                    placeholder="Email"
                                    required
                                    id="email"
                                />
                                <label for="email" class="form__label">Email</label>
                            </div>
                            <div class="form__group u-margin-bottom-medium">
                                <div class="form__radio-group">
                                    <input
                                        type="radio"
                                        class="form__radio-input"
                                        id="small"
                                        name="size"
                                    />
                                    <label for="small" class="form__radio-label">
                                        <span class="form__radio-button"></span>small tour
                                        group</label
                                    >
                                </div>
                                <div class="form__radio-group">
                                    <input
                                        type="radio"
                                        class="form__radio-input"
                                        id="large"
                                        name="size"
                                    />
                                    <label for="large" class="form__radio-label">
                                        <span class="form__radio-button"></span>large tour
                                        group</label
                                    >
                                </div>
                            </div>
                            <div class="form__group">
                                <button type="submit" class="btn btn--green u-center-text">
                                    next step&rarr;
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>

            {/* {isLoginMode ? (<form onSubmit={loginUser} >
                    <label htmlFor="email"
                        className='form__label'>Email</label>
                    <input
                        type="email"
                        name="email"
                        className='form__input'
                        placeholder="abc@example.com"
                        onChange={hadleChange}
                        value={userDetails.email}
                    />
                    <label htmlFor="password" className='form__label'>Password</label>
                    <input
                        type="password"
                        name="password"
                        className='form__input'
                        placeholder="123gggewr"
                        onChange={hadleChange}
                    />

                    <button type="submit" className='btn btn--green' >{isLoginMode ? 'Login' : 'Sign-Up'}</button>
                </form>) : (<Register />)} */}

        </>
    );
};


export default withRouter(LoginPage);
