import React, { useState, useEffect } from 'react'
import Multiselect from 'multiselect-react-dropdown';
// import Button from '../FormElements/Button';
import Card from '../components/UIElemets/Card'
import { useHistory } from 'react-router-dom'
const { getUserContacts, addConversation } = require('../DAL/index')
export default function GroupPage() {
    const [selectedValues, setSelectedValues] = useState(true)
    const [name, setName] = useState('')
    const [contacts, setContacts] = useState([])
    const [errorMessage, setErrorMessage] = useState('')
    const history = useHistory()
    const userId = localStorage.getItem('userId')
    const userName = localStorage.getItem('userName')
    const handleSubmit = async (e) => {
        e.preventDefault()
        const result = await addConversation(name, [...selectedValues, { name: userName, userId }], userId)
        if (!result.message) {
            history.push(`chatroom/${result._id}`)
        }
    }
    useEffect(async () => {
        const userContacts = await getUserContacts(userId)
        setContacts(userContacts)
    }, [])
    const hadleChange = (e) => {
        setName(e.target.value)
    }
    return (
        <Card>
            <form onSubmit={handleSubmit}>
                <div className="form-control">
                    <label htmlFor="name">group Name</label>
                    <input
                        type="text"
                        name="name"
                        id="name"
                        placeholder="sport room"
                        onChange={hadleChange}
                    />
                </div>
                <Multiselect options={contacts}
                    selectedValue={contacts}
                    onSelect={(data) => { setSelectedValues(data) }}
                    onRemove={(data) => { setSelectedValues(data) }}
                    displayValue={'name'}

                />
                <button type="submit" >Create</button>
            </form>
        </Card >
    )
}
