// import thunk from 'redux-thunk'

const redux = require('redux')
const { logger } = require('redux-logger')
const thunkMiddleware = require('redux-thunk').default
const { cakeReducer } = require('./cakes/cakeReducer')
const { buyCake } = require('../redux/cakes/cakeActions')
const { createStore, applyMiddleware } = require('redux')

const { composeWithDevTools } = require('redux-devtools-extension')
const thunk = require('redux-thunk').default
const rootReducer = require('./rootReducer')
const userReducer = require('./user/reducer')

// const store = createStore(userReducer, applyMiddleware(thunkMiddleware, thunk))
const store = createStore(
    rootReducer, composeWithDevTools(applyMiddleware(logger, thunk))
)
// console.log('initial state', store.getState())
// const unsubcribe = store.subscribe(() => console.log('updated values', store.getState()))


// module.exports = store
export default store
