import React, { useState, useEffect } from 'react'
import io from 'socket.io-client'
import socketIOClient from 'socket.io-client'
import { getRoomChat, afterPostMessage } from '../redux/conversations/actions'
import { connect, useSelector, useDispatch } from 'react-redux'
function LiveChat() {
    let socket
    const [message, setMessage] = useState('')
    const [typing, setTyping] = useState(false)
    const { messages, members, loading, error, name2, currentRoom } = useSelector(state => state.conversations)
    const { user } = useSelector(state => state.user)
    const dispatch = useDispatch()
    const userId = localStorage.getItem('userId')
    const name = localStorage.getItem('userName')
    const chatroomId = currentRoom

    useEffect(() => {
        socket = socketIOClient.connect("http://localhost:5000");
        console.log('socket', socket)
        if (socket) {
            socket.emit("joinRoom", {
                chatroomId, userId
            });

            socket.on("newMessage", ({ newMessage }) => {
                dispatch(afterPostMessage(newMessage))
            });
            socket.on("typing", user => {
                setTyping(`${user} is typing...`)
            })
            socket.emit("joinRoom", {
                chatroomId, userId
            });

        }
        return () => {
            //Component Unmount
            if (socket) {
                socket.emit("leaveRoom", {
                    chatroomId,
                });
            }
        };

    }, []);

    useEffect(() => {

        setTimeout(() => {
            setTyping(false)
        }, 5000)
    }, [typing])

    const sendUserAction = (e) => {
        const user = localStorage.getItem('userName')
        setMessage(e.target.value)
        if (socket) {
            socket.emit('typing', user)
        }
    }
    const sendMessage = () => {
        const chatMessage = {
            chatroomId: currentRoom,
            message: { userId: localStorage.getItem('userId'), name, content: message, date: Date.now() },
            userId: localStorage.getItem('userId')
        }
        if (socket) {
            socket.emit("chatroomMessage", chatMessage);

            setMessage("");

        }
    };
    return (
        <>
            {loading && <li>loading</li>}
            {error && <li>{error}</li>}
            {name && <h2>{name}</h2>}
            {typing && <p>{typing}</p>}
            {messages.length === 0 ? <p>there is no conversations</p> : (<div className="chatroomContent">
                {messages.map((message, i) => (

                    <div key={i} className="message">
                        <span
                            className={
                                ' ' === message.userId ? "ownMessage" : "otherMessage"
                            }
                        >
                            {message.userId === '' ? message.viewers === true ? 'VV  ' : 'V ' : ''}
                            {message.name}:
                        </span>{" "}
                        {message.content}
                    </div>
                ))}
            </div>)}
            <div class="row">
                    <div class="story">
                        <figure class="story__shape">
                            <img src={message.image} alt={`message.name`} class="story__img"/>
                            <figcaption class="story__caption">{message.name}</figcaption>
                        </figure>
                        <div class="story__text">
                            <h3 class="heading-tertiary u-margin-bottom-small">I had the best week ever with my family</h3>
                            <p>
                                Lorem ipsum dolor sit amet consectetur adipisicing elit. Aperiam, ipsum sapiente aspernatur libero repellat quis consequatur
                                ducimus quam nisi exercitationem omnis earum qui. Aperiam, ipsum sapiente aspernatur libero
                                repellat quis consequatur ducimus quam nisi exercitationem omnis earum qui.
                            </p>
                        </div>
                    </div>
                </div>
            <div className="chatroomActions">
                <div>
                    <input
                        type="text"
                        name="message"
                        placeholder="Say something!"
                        onChange={sendUserAction}
                        value={message}
                    />
                </div>
                <div>
                    <button className="join" onClick={sendMessage}>
                        Send
                    </button>
                </div>
            </div>
        </>
    )
}

export default LiveChat
