import React, { useState, useEffect } from 'react'
import { withRouter, useHistory } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import { login, signup } from '../redux/user/actions'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee, } from '@fortawesome/free-solid-svg-icons'
import Card from '../components/UIElemets/Card';
import Register from './Register';
const LoginPage = ({ setupSocket }) => {
    const dispatch = useDispatch()
    const { user, error, loading } = useSelector(state => state.user)
    const [isLoginMode, setIsLoginMode] = useState(true)   // const auth = useContext(AuthContext)
    const [userDetails, setUseDetails] = useState({})
    const history = useHistory()
    const [errorMessage, setErrorMessage] = useState('')


    const hadleChange = (e) => {
        setUseDetails({ ...userDetails, [e.target.name]: e.target.value })
    }

    useEffect(() => {
        if (user) {
            setupSocket()
            history.push("/conversations");
        }
    }, [])
    useEffect(() => {
        if (user) {
            history.push("/conversations");
        } else {
            setErrorMessage(error)
        }
    }, [user, error, loading])
    const loginUser = async (e) => {
        console.log('user', e)
        e.preventDefault()
        const { email, password } = userDetails
        if (isLoginMode) {
            dispatch(login(email, password))
        } else {
            dispatch(signup(userDetails))
        }
    };
    const switchModehandler = () => {
        setIsLoginMode(!isLoginMode)
    }

    return (
        < >
            <h2 >{isLoginMode ? 'Login' : 'Sign-Up'}</h2>
            {errorMessage && <p>{errorMessage}</p>}
            {loading && <p>loading</p>}
            <hr />
            <div className="container-fluid">
                <div className="row main-content bg-success text-center">
                    <div className="col-md-4 text-center company__info">

                        <span className="company__logo"><h2><span className="fa fa-android" icon='coffee'></span></h2></span>
                        <h4 className="company_title">Your Company Logo <FontAwesomeIcon icon={faCoffee} /></h4>
                        <i class="fas fa-sync"></i>
                    </div>
                    <div className="col-md-8 col-xs-12 col-sm-12 login_form ">
                        <div className="container-fluid">
                            <div className="row">
                                <h2>Log In</h2>
                                Your <FontAwesomeIcon icon="coffee" /> is hot and ready!
                            </div>
                            <div className="row">
                                <form control="" className="form-group">
                                    <div className="row">
                                        <input type="text" name="username" id="username" className="form__input" placeholder="Username" />
                                        <label htmlFor="username" className="form__label">userName</label>
                                    </div>
                                    <div className="row">
                                        <span className="fa fa-lock"></span>
                                        <input type="password" name="password" id="password" className="form__input" placeholder="Password" />
                                        <label htmlFor="username" className="form__label">Pssword</label>
                                    </div>
                                    <div className="row">
                                        <input type="checkbox" name="remember_me" id="remember_me" className="" />
                                        <label for="remember_me">Remember Me!</label>
                                    </div>
                                    <div className="row">
                                        <input type="submit" value="Submit" className="btn" />
                                    </div>
                                </form>
                            </div>
                            <div className="row">
                                <p>Don't have an account? <a href="#">Register Here</a></p>
                            </div>
                        </div>
                    </div>
                </div>
                <button inverse onClick={switchModehandler} className='btn' value="Submit">Switch to {!isLoginMode ? 'Login' : 'Sign-Up'}</button>
            </div>


            {/* {isLoginMode ? (<form onSubmit={loginUser} >
                    <label htmlFor="email"
                        className='form__label'>Email</label>
                    <input
                        type="email"
                        name="email"
                        className='form__input'
                        placeholder="abc@example.com"
                        onChange={hadleChange}
                        value={userDetails.email}
                    />
                    <label htmlFor="password" className='form__label'>Password</label>
                    <input
                        type="password"
                        name="password"
                        className='form__input'
                        placeholder="123gggewr"
                        onChange={hadleChange}
                    />

                    <button type="submit" className='btn btn--green' >{isLoginMode ? 'Login' : 'Sign-Up'}</button>
                </form>) : (<Register />)} */}

        </>
    );
};


export default withRouter(LoginPage);
