import React, { useState,useEffect } from 'react'
import { withRouter, useHistory } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import { login, signup } from '../redux/user/actions'
import Card from '../components/UIElemets/Card';
const LoginPage = ({ setupSocket }) => {
    const dispatch = useDispatch()
    const { user, error, loading } = useSelector(state => state.user)
    const [isLoginMode, setIsLoginMode] = useState(true)   // const auth = useContext(AuthContext)
    const [userDetails, setUseDetails] = useState({})
    const history = useHistory()
    const [errorMessage, setErrorMessage] = useState('')


    const hadleChange = (e) => {
        setUseDetails({ ...userDetails, [e.target.id]: e.target.value })
    }

    useEffect(() => {
        if (user) {
            setupSocket()
            history.push("/conversations");
        }
    }, [])
    useEffect(() => {
        if (user) {
            history.push("/conversations");
        } else {
            setErrorMessage(error)
        }

    }, [user, error, loading])
    const loginUser = async (e) => {
        console.log('user', e)
        e.preventDefault()
        const { email, password } = userDetails
        if (isLoginMode) {
            dispatch(login(email, password))


            // const result = await login(userDetails.email, userDetails.password)
            // if (!result.message) {
            //     localStorage.setItem("CC_Token", result.token);
            //     localStorage.setItem("userId", result._id);
            //     localStorage.setItem("userName", result.firstName);
            //     auth.login()
            //     history.push("/dashboard");
            // } else {
            //     setErrorMessage(result.message)
            // }
        } else {
            dispatch(signup(userDetails))
            // const result = await signUp({ ...userDetails })
            // if (!result.message) {
            //     setIsLoginMode(true)
            // } else {
            //     setErrorMessage(result.message)
            // }


        }
    };
    const switchModehandler = () => {
        setIsLoginMode(!isLoginMode)
    }

    return (
        <Card >
            <h2 >{isLoginMode ? 'Login' : 'Sign-Up'}</h2>
            {errorMessage && <p>{errorMessage}</p>}
            {loading && <p>loading</p>}
            <hr />
            <form onSubmit={loginUser}>
                <div className="form-control">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        name="email"
                        id="email"
                        placeholder="abc@example.com"
                        onChange={hadleChange}
                        value={userDetails.email}
                    />
                </div>
                <div className="form-control">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        name="password"
                        id="password"
                        placeholder="123gggewr"
                        onChange={hadleChange}
                    />
                </div>
                {!isLoginMode && <>
                    <div className="form-control">
                        <label htmlFor="firstName">first Name</label>
                        <input
                            type="text"
                            name="firstName"
                            id="firstName"
                            placeholder="Yehuda"
                            onChange={hadleChange}
                            value={userDetails.firstName}
                        />
                    </div>
                    <div className="form-control">
                        <label htmlFor="lastName">last Name</label>
                        <input
                            type="text"
                            name="lastName"
                            id="lastName"
                            placeholder="Cohen"
                            onChange={hadleChange}
                        />
                    </div>
                </>
                }

                <button type="submit" >{isLoginMode ? 'Login' : 'Sign-Up'}</button>
            </form>
            <button inverse onClick={switchModehandler}>Switch to {!isLoginMode ? 'Login' : 'Sign-Up'}</button>
        </Card>
    );
};

const mapStateToProps = state => {
    return {
        userData: state.user
    }
}
const mapDispatchToProps = dispatch => {
    return {
        login: (email, password) => dispatch(login(email, password))
    }
}

export default withRouter(LoginPage);
