            </div>
            <div >
                {conversations && conversations.map((item, index) => (
                    <li key={index} className={item.cName || 'nav-list'}>
                        <p onClick={() => joinToRoom(item._id)} >
                            {item.icon}
                            <span>{item.name}</span>
                        </p>
                    </li>

                ))}
            </div>
            <div class="popup" id="popup">
                <div class="popup__content">
                    <div class="popup__left">
                      add conversation
