import React from "react";
import { BrowserRouter as Router, Switch, Route, Redirect } from "react-router-dom";
import LoginPage from "./Pages/LoginPage";
import RegisterPage from "./Pages/RegisterPage";
import DashboardPage from "./Pages/DashboardPage";
import IndexPage from "./Pages/IndexPage";
import ChatRoomPage from "./Pages/ChatRoomPage";
import io from "socket.io-client";
import makeToast from "./Toaster";
import NavLinks from "./components/Navigation/NavLinks";
import MainNavigation from "./components/Navigation/MainNavigation";
import store from './redux/store'
import { Provider, useSelector } from 'react-redux'
import GroupPage from '../src/Pages/GroupPage'
import Register from "./Pages/Register";
import Contacts from "./Pages/Contacts";
import LiveChat from "./Pages/LiveChat";
function App() {
  const { user } = useSelector(state => state.user)
  const [socket, setSocket] = React.useState(null);
  const setupSocket = () => {
    const token = localStorage.getItem("token");
    if (token && !socket) {
      const newSocket = io("http://localhost:5000", {
        query: {
          token: localStorage.getItem("token"),
        },
      });
      newSocket.on('connection',()=>{

      })
      newSocket.on("disconnect", () => {
        setSocket(null);
        // setTimeout(setupSocket, 3000);
        makeToast("error", "Socket Disconnected!");
      });

      setSocket(newSocket);
    }
  };

  React.useEffect(() => {
    setupSocket();
    return () => { }
  }, []);
  const isLoggedIn = user

  let routes
  if (isLoggedIn) {
    routes = (
      <Switch>
        <Route path="/conversations" render={() => <DashboardPage />} exact socket={socket} />
        {/* <Route path="/mycontacts" render={() => <Contacts socket={socket} />} exact /> */}
        <Route path="/chatroom" render={() => <LiveChat socket={socket} />} exact />
        <Route
          path="/dashboard"
          render={() => <DashboardPage socket={socket} />}
          exact
        />
        <Route
          path="/chatroom/:id"
          render={() => <ChatRoomPage socket={socket} />}
          exact
        />
        <Route
          path="/contacts"
          render={() => <Contacts socket={socket} />}
          exact
        />
        <Route
          path="/signup"
          exact
          render={() => <Register socket={socket} />}
        />
        <Route path="/group" exact>
          <GroupPage />
        </Route>
        <Redirect to="/" />
      </Switch >
    );
  } else {
    routes = (<Switch>
      <Route
        path="/"
        render={() => <DashboardPage socket={socket} />}
        exact
      />
      <Route path="/register" component={RegisterPage} exact />
      <Route path="/login" render={() => <LoginPage setupSocket={setSocket} />} exact />
      <Redirect to="/" />

    </Switch>
    );
  }
  return (
    <Provider store={store}>
      <Router>
        <NavLinks />
        <main>
          {routes}
        </main>
      </Router>
    </Provider>
  );
}

export default App;
