import { useState } from 'react';
// import { SendOutlined, PictureOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';
import axios from 'axios';
const isTyping = () => { }
const MessageForm = (props) => {
    const [value, setValue] = useState('');
    const { chatId, creds } = props;
    const { currentRoom } = useSelector(state => state.conversations)
    const handleChange = (event) => {
        setValue(event.target.value);

        isTyping(props, chatId);
    };



    const sendMessage = (e) => {
        e.preventDefault()
        const chatMessage = {
            chatroomId: currentRoom,
            message: { userId: localStorage.getItem('userId'), content: value, date: Date.now() },
            userId: localStorage.getItem('userId')
        }
        axios.post('http://localhost:5000/api/conversation/send', { cahtId: currentRoom, chat: value }).then(response => console.log(response))
        // if (socket) {
        //     socket.emit("chatroomMessage", chatMessage);
        //     setMessage("");
        // }
    };
    const handleSubmit = (event) => {
        event.preventDefault();

        const text = value.trim();

        if (text.length > 0) {
            sendMessage(creds, chatId, { text });
        }

        setValue('');
    };

    const handleUpload = (event) => {
        sendMessage(creds, chatId, { files: event.target.files, text: '' });
    };

    return (
        <form className="message-form" onSubmit={handleSubmit}>
            <input
                className="message-input"
                placeholder="Send a message..."
                value={value}
                onChange={handleChange}
                onSubmit={handleSubmit}
            />
            <label htmlFor="upload-button">
                <span className="image-button">
                    picture
                </span>
            </label>
            <input
                type="file"
                multiple={false}
                id="upload-button"
                style={{ display: 'none' }}
                onChange={handleUpload.bind(this)}
            />
            <button type="submit" className="send-button">
                send
            </button>
        </form>
    );
};

export default MessageForm;
