import React, { useEffect } from "react";
import { withRouter } from "react-router-dom";
import io from "socket.io-client";

import socketIOClient from 'socket.io-client'
const { getRoomChat } = require('../DAL/index')
let socket;
const CONNECTION_PORT = "http://localhost:5000";
const ChatroomPage = ({ match }) => {
    const chatroomId = match.params.id;
    const [messages, setMessages] = React.useState([]);
    const messageRef = React.useRef();
    const [roomName, setRoomName] = React.useState('')
    const [typing, setTyping] = React.useState("");
    const userId = localStorage.getItem('userId')
    const name = localStorage.getItem('userName')
    useEffect(async () => {
        // socket = io(CONNECTION_PORT)
        if (socket) {
            socket.on("newMessage", async ({ newMessage }) => {
                const getMessages = await getRoomChat(chatroomId)
                setMessages(getMessages.messages)
                socket.emit('watchMessage', {
                    chatroomId, userId, messageId: newMessage["_id"]
                })
            });
            socket.on("typing", user => {
                setTyping(`${user} is typing...`)
            })
            // socket.on("watchMessage", async chatroomId => {
            //     const getMessages = await getRoomChat(chatroomId)
            //     setMessages(getMessages.messages)
            //     console.log(messages)
            // })
        }
    }, [messages]);
    const sendMessage = () => {
        if (socket) {
            socket.emit("chatroomMessage", {
                chatroomId: chatroomId,
                message: { userId, name, content: messageRef.current.value, date: Date.now() },
                userId: localStorage.getItem('userId')
            });
            messageRef.current.value = "";
        }
    };

    const sendUserAction = () => {
        if (socket) {
            socket.emit('typing', {
                name
            })
        }
    }

    React.useEffect(async () => {
        socket = socketIOClient("http://localhost:5000");
        const getMessages = await getRoomChat(chatroomId)
        setMessages(getMessages.messages)
        setRoomName(getMessages.name)
        if (socket) {
            socket.emit("joinRoom", {
                chatroomId, userId
            });
        }

        return () => {
            //Component Unmount
            if (socket) {
                socket.emit("leaveRoom", {
                    chatroomId,
                });
            }
        };

    }, []);
    useEffect(() => {
        setTimeout(() => {
            setTyping('')
        }, 5000)
    }, [typing])

    return (
        <div className="chatroomPage">
            {typing && <div className="typind">{typing}</div>}
            <div className="chatroomSection">
                <div className="cardHeader">{roomName ? roomName : 'Chatroom Name'}</div>
                <div className="chatroomContent">
                    {messages.map((message, i) => (

                        <div key={i} className="message">
                            <span
                                className={
                                    userId === message.userId ? "ownMessage" : "otherMessage"
                                }
                            >
                                {message.userId === userId ? message.viewers === true ? 'VV  ' : 'V ' : ''}
                                {message.name}:
                            </span>{" "}
                            {message.content}
                        </div>
                    ))}
                </div>
                <div className="chatroomActions">
                    <div>
                        <input
                            type="text"
                            name="message"
                            placeholder="Say something!"
                            onChange={sendUserAction}
                            ref={messageRef}
                            onUnFocuced
                        />
                    </div>
                    <div>
                        <button className="join" onClick={sendMessage}>
                            Send
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default withRouter(ChatroomPage);
