import io from "socket.io-client";
const { SIGNUP_SUCCESS, SIGNUP_FAILURE,
    SIGNUP_REQUEST, LOGIN_FAILURE, LOGIN_SUCCESS, REQUEST } = require('../user/types')
const initialState = {
    loading: false,
    user: localStorage.getItem('userId'),
    error: '',
    socket: ''
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case REQUEST:
            return {
                ...state,
                loading: true
            }
        case LOGIN_SUCCESS:
            return {
                loading: false,
                user: action.payload,
                error: ''
            }
        case SIGNUP_SUCCESS:
            return {
                loading: false,
                user: action.payload,
                error: '',
                socket: setupSocket
            }
        case LOGIN_FAILURE:
            return {
                loading: false,
                user: '',
                error: action.payload
            }

        case SIGNUP_FAILURE:
            return {
                loading: false,
                user: '',
                error: action.payload
            }
        default: return state
    }
}

export function setupSocket () {
    const token = localStorage.getItem("token");
    if (token && !socket) {
        const newSocket = io("http://localhost:5000", {
            query: {
                token: localStorage.getItem("token"),
            },
        });

        newSocket.on("disconnect", () => {
           
            setTimeout(setupSocket, 3000);
            makeToast("error", "Socket Disconnected!");
        });

       
    }
};
module.exports = reducer
