const { BUY_CAKE } = require('./cakeTypes')
const initialState = {
    numOfCakes: [1, 2, 3]
}
const cakeReducer = (state = initialState, action) => {
    switch (action.type) {
        case BUY_CAKE: return {
            ...state, numOfCakes: state.numOfCakes - action.payload
        }
        default: return state
    }

}
module.exports = cakeReducer
